t.script:
function toggleView(view) {
    const container = document.getElementById('hotel-view');
    const gridBtn = document.getElementById('gridViewBtn');
    const listBtn = document.getElementById('listViewBtn');

    if (view === 'list') {
        container.classList.remove('hotel-grid-view');
        container.classList.add('hotel-list-view');

        listBtn.classList.add('active');
        gridBtn.classList.remove('active');
    } else {
        container.classList.remove('hotel-list-view');
        container.classList.add('hotel-grid-view');

        gridBtn.classList.add('active');
        listBtn.classList.remove('active');
    }
}
